import React from 'react';
import Box from '@mui/material/Box';
import Grid from '@mui/material/Grid';
import ReadTable from './ReadTable';



function DailyTaskForm(props) {

    const [inputarr,
        setInputarr] = React.useState([])

    const [inputdata,
        SetInputdata] = React.useState({
            task_name: "",
            category: "",
            jira_id: "",
            Assignee: "",
            Status: "",
            due_date: "",
            go_live: "",
            mock_up: "",
            other_comments: ""
        })
    const [editFormData, setEditFormData] = React.useState({
        task_name: "",
        category: "",
        jira_id: "",
        Assignee: "",
        Status: "",
        due_date: "",
        go_live: "",
        mock_up: "",
        other_comments: ""
    });

    function handleChange(e) {

        SetInputdata({
            ...inputdata,
            [e.target.name]: e.target.value
        })

    }

    let { task_name,
        category,
        jira_id, Assignee,
        Status,
        due_date,
        go_live,
        mock_up,
        other_comments } = inputdata;
    function changhandle() {
        setInputarr([
            ...inputarr, {
                task_name,
                category,
                jira_id, Assignee,
                Status,
                due_date,
                go_live,
                mock_up,
                other_comments
            }
        ])
        SetInputdata({
            task_name: "",
            category: "",
            jira_id: "",
            Assignee: "",
            Status: "",
            due_date: "",
            go_live: "",
            mock_up: "",
            other_comments: ""
        })

    }
    const handleEditFormChange = (event) => {
        event.preventDefault();

        const fieldName = event.target.getAttribute("name");
        const fieldValue = event.target.value;

        const newFormData = { ...editFormData };
        newFormData[fieldName] = fieldValue;

        setEditFormData(newFormData);
    };
    const handleEditFormSubmit = (event) => {
        event.preventDefault();

        const editedContact = {
            id: editContactId,
            fullName: editFormData.fullName,
            address: editFormData.address,
            phoneNumber: editFormData.phoneNumber,
            email: editFormData.email,
        };

        const newContacts = [...contacts];

        const index = contacts.findIndex((contact) => contact.id === editContactId);

        newContacts[index] = editedContact;

        setContacts(newContacts);
        setEditContactId(null);
    };
    const handleEditClick = (event, contact) => {
        event.preventDefault();
        setEditContactId(contact.id);

        const formValues = {
            fullName: contact.fullName,
            address: contact.address,
            phoneNumber: contact.phoneNumber,
            email: contact.email,
        };

        setEditFormData(formValues);
    };
    return (
        <>
            <Box className='section'>
                <Box className='container task-form'>
                    <Grid container spacing={2}>
                        <Grid item md={5} >
                            <input type="text" name='task_name' onChange={handleChange} value={inputdata.task_name} placeholder='Task Name' />
                        </Grid>
                        <Grid item md={5}>
                            <select name="category" value={inputdata.category} onChange={handleChange}>
                                <option value="">Select Cotegory</option>
                                <option value="Nap Bug Task">Bug Task</option>
                                <option value="Nap Dev">Dev</option>
                                <option value="Nap Dev">staging</option>
                            </select>
                        </Grid>
                        <Grid item md={2}>
                            <input type="text" onChange={handleChange} value={inputdata.jira_id} name='jira_id' placeholder='Jira ID' />
                        </Grid>
                    </Grid>
                    <Grid container spacing={2} mt={1}>
                        <Grid item md={3} >
                            <select name="Assignee" value={inputdata.Assignee} onChange={handleChange}>
                                <option value="">Select Assignee</option>
                                <option value="Rohan">Rohan</option>
                                <option value="Kriti">Kriti</option>
                                <option value="Archana">Archana</option>
                            </select>
                        </Grid>
                        <Grid item md={3}>
                            <select name="Status" value={inputdata.Status} onChange={handleChange}>
                                <option value="">Select Status</option>
                                <option value="Completed">Completed</option>
                                <option value="Pending">Pending</option>
                                <option value="Processing">Processing</option>
                            </select>
                        </Grid>
                        <Grid item md={3}>
                            <input type="date" value={inputdata.due_date} name='due_date' onChange={handleChange} />
                        </Grid>
                        <Grid item md={3}>
                            <input type="date" value={inputdata.go_live} name='go_live' onChange={handleChange} />
                        </Grid>
                    </Grid>
                    <Grid container spacing={2} mt={1}>
                        <Grid item md={12}>
                            <input type="textarea" value={inputdata.mock_up} name='mock_up' onChange={handleChange} placeholder='Mock UP' />
                        </Grid>
                    </Grid>
                    <Grid container spacing={2} mt={1}>
                        <Grid item md={12}>
                            <textarea name='other_comments' value={inputdata.other_comments} onChange={handleChange} placeholder='Other Comments' rows="4" />
                            <button onClick={changhandle} className="w-100">Submit</button>
                        </Grid>
                    </Grid>
                </Box>
            </Box>
            <form onSubmit={handleEditFormSubmit}>
                <table>
                    <thead>
                        <tr>
                            <th>Name</th>
                            <th>Address</th>
                            <th>Phone Number</th>
                            <th>Email</th>
                            <th>Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {inputarr.map((item) => (
                            <Fragment>
                                    {/* <EditableRow
                                        editFormData={editFormData}
                                        handleEditFormChange={handleEditFormChange}
                                        handleCancelClick={handleCancelClick}
                                    /> */}
                                    <ReadTable
                                        data={item}
                                        // handleEditClick={handleEditClick}
                                        // handleDeleteClick={handleDeleteClick}
                                    />
                            </Fragment>
                        ))}
                    </tbody>
                </table>
            </form>
        </>


    );
}

export default DailyTaskForm;
