import React, { Component } from 'react';
import DailyTaskForm from './Component/DailyTaskForm';
import './Scss/style.css';

function App(){
  const [inputarr,
    setInputarr] = useState([])

const [inputdata,
    SetInputdata] = useState({name: "", rollNo: ""})

function changehandle(e) {

    SetInputdata({
        ...inputdata,
        [e.target.name]: e.target.value
    })

}

let {name, rollNo} = inputdata;
function changhandle() {
    setInputarr([
        ...inputarr, {
            name,
            rollNo
        }
    ])

    console.log(inputdata, "input data what we Enter")
    SetInputdata({name: "", rollNo: ""})

}
    return (
      <div className="App">
        {/* <DailyTaskForm/> */}
        <input
                type="text"
                autoComplete='off'
                name='name'
                value={inputdata.name}
                onChange={changehandle}
                placeholder="Enter Name"/>
            <input
                type="text"
                autoComplete='off'
                name='rollNo'
                value={inputdata.rollNo}
                onChange={changehandle}
                placeholder="Roll no"/>

            <button onClick={changhandle}>Add It
            </button><br/>
      </div>
    );
}

export default App;
