import React from 'react';
import Button from '@mui/material/Button';
import Grid from '@mui/material/Grid';
import { product } from '../core/product';



function ShopHome(props) {
    const n = 2;
    return (
        <div className="section">
            <div className="container">
                <div className="shop-home">
                    <div className="heading text-center">
                        <h1>Shop Home Bars</h1>
                        <h3>Choose from a variety of different styles to suit your home</h3>
                    </div>
                    <Grid container spacing={3} mt={4}>
                        {product.map((item, i) =>
                            <Grid item md={4} mb={4}>
                                <div className="product-details">
                                    <img src={item.img} alt="" />
                                    <p><strike>{item.offPrice}</strike></p>
                                    <h2>{item.mainPrice}</h2>
                                    <h3>{item.productName}</h3>
                                    <Button variant='contained'>Buy Now ></Button>
                                </div>
                            </Grid>
                        )}
                    </Grid>
                </div>
                <Grid container spacing={10} mt={2} className="text-center">
                    {
                        [...Array(n)].map((item, i) =>
                            <Grid item md={6} mb={4} key={i}>
                                <div className="dummy-content">
                                    <img src="/images/3.jpg" alt="" />
                                    <h2 className='mb-1 mt-1'>lorem Dummy text</h2>
                                    <p>Lorem ipsum dolor sit, amet consectetur adipisicing elit. Corporis, veritatis suscipit! Quae pariatur sed fugit, optio omnis architecto, sequi aspernatur ea ab mollitia minima. Itaque rem illo et reiciendis officiis.</p>
                                </div>
                            </Grid>
                        )
                    }
                </Grid>
                <Grid container spacing={2} mt={4} className="align-content">
                    {
                        [...Array(n)].map((item, i) =>
                            <>
                                <Grid item md={6} mb={4}>
                                    <div className="dummy-content-img">
                                        <img src="/images/3.jpg" alt="" />
                                    </div>
                                </Grid>
                                <Grid item md={6} mb={4} >
                                    <h2 className='mb-1 mt-1'>lorem Dummy text</h2>
                                    <p>Lorem ipsum dolor sit, amet consectetur adipisicing elit. Corporis, veritatis suscipit! Quae pariatur sed fugit, optio omnis architecto, sequi aspernatur ea ab mollitia minima. Itaque rem illo et reiciendis officiis.</p>
                                </Grid>
                            </>

                        )
                    }
                </Grid>
            </div>
        </div>
    );
}

export default ShopHome;
